mapper.insert:
insert into association ( name, details,simple_detail,user_id
  )
values ( #{name}, #{details},#{simple_detail},#{user_id}
  )

mapper.select:
SELECT a.*
FROM association a
WHERE a.delete_status=1 and a.id=#{id}

mapper.update:
UPDATE association SET name=#{name}, details=#{details}, simple_detail=#{simple_detail},
delete_status=#{delete_status，jdbcType=INTEGER},
user_id=#{user_id} WHERE id = #{id}

update sys_user
set
