mapper.select:
SELECT count(0)
FROM ass_users w
WHERE w.delete_status = 1 AND w.user_id=#{user_id}

SELECT count(0)
FROM ass_users w
WHERE w.delete_status = 1 AND w.user_id=#{user_id} AND association_id=#{association_id}

SELECT count(0)
FROM association w
WHERE w.delete_status = 1 AND w.user_id=#{user_id} AND id=#{association_id}

select au.id,au.user_name,au.user_id,au.association_id,au.association_name,au.activity_name,au.activity_id,au.score, date_format(au.start_time, '%Y.%m.%d %T') start_time, date_format(au.end_time, '%Y.%m.%d %T') end_time from act_ass_user au WHERE delete_status=1
and user_id=#{user_id}
and association_id=#{association_id}
ORDER BY id DESC LIMIT #{offSet}, #{pageRow}

select count(0) from act_ass_user


        user_id=#{user_id}


        association_id=#{association_id}

mapper.update:
UPDATE article
   SET
    content = #{content}
WHERE id = #{id}
